import React, { Component } from 'react';
import FlightItem from '../flight-item';

import './flights-table.css';

export default class FlightsTable extends Component {

  state = {
    items: null
  };

  rollingItems(data) {
    const items = data.map((item) => {
      const { id, ...itemProps } = item;

      return (
        <li key={id} className='list-group-item'>
          <FlightItem 
            { ...itemProps } 
          />
        </li>
      );
    });

    return items;
  }

  render() {
    const flightItems = this.props.flightsData;

    if (flightItems) {
      this.setState(() => {
        const items = this.rollingItems(flightItems);
        return {
          items
        }
      });
    }

    return (
      <div className='flights-table'>
        <div className='flights-table-list'>
          <ul className='list-group'>
               { this.state.items }
          </ul>
        </div>
      </div>
    );
  }
};
