import React, { Component } from 'react';
import YandexFlightsShedule from '../../services/yandex-flights-shedule';

import './flight-item.css';

export default class FlightItem extends Component {

	apiService = new YandexFlightsShedule();

	state = {
		station: 'default',
		direction: 'default',
		flight: 'default',
		carrier: 'default',
		time: 'default'
	};

	constructor() {
		super();
		this.updateItem();
	}

	updateItem() {
		this.apiService.getArrivals()
			.then((body) => {
				const details = body.schedule[0].thread;
				const { number, title } = details;
				console.log(body);
				this.setState({
					station: body.station.title,
					direction: title,
					flight: number
				});
			});
	}


	render() {
		return (
			<span className='flight-item'>
				<span className='flight-item-sub'>№ Рейса: { this.state.flight }</span>
				<span className='flight-item-sub'>{ this.state.direction }</span>
				<span className='flight-item-sub'>{ this.state.station }</span>
				<span className='flight-item-sub'>{ this.state.carrier }</span>
				<span className='flight-item-sub'>{ this.state.carrier }</span>

			</span>
		);
	};
};
