import React, { Component } from 'react';
import YandexFlightsShedule from '../../services/yandex-flights-shedule';

import './flight-item.css';

export default class FlightItem extends Component {

	apiService = new YandexFlightsShedule();

	state = {
		event: 'default',
		time: 'default',
		direction: 'default',
		flight: 'default',
		carrier: 'default',
	};

	constructor() {
		super();
		this.updateItem();
	}

	updateItem() {
		this.apiService.getDepartures()
			.then((body) => {

				console.log(body);

			});
	}


	render() {
		return (
			<span className='flight-item'>
				<span className='flight-item-sub'>{ this.state.event }</span>
				<span className='flight-item-sub'>{ this.state.time }</span>
				<span className='flight-item-sub'>{ this.state.direction }</span>
				<span className='flight-item-sub'>{ this.state.flight }</span>
				<span className='flight-item-sub'>{ this.state.carrier }</span>
			</span>
		);
	};
};
