import React, { Component } from 'react';

import './flight-item.css';

export default class FlightItem extends Component {


	state = {
		id: null,
		event: null,
		time: null,
		direction: null,
		flight: null,
		carrier: null,
		vehicle: null,
		days: null
	};

	updateItem() {
		this.apiService.getDepartures()
			.then((body) => {				
				console.log(body);
				this.setState({
					id: body[8].id,
					event: body[8].event,
					time: body[8].time,
					direction: body[8].direction,
					flight: body[8].flight,
					carrier: body[8].carrier,
					vehicle: body[8].vehicle,
					days: body[8].days
				});			
			});
	}


	render() {
		return (
			<span className='flight-item'>
				<span className='flight-item-sub'>{ this.state.event }</span>
				<span className='flight-item-sub'>{ this.state.time }</span>
				<span className='flight-item-sub'>{ this.state.direction }</span>
				<span className='flight-item-sub'>{ this.state.flight }</span>
				<span className='flight-item-sub'>{ this.state.carrier }</span>
				<span className='flight-item-sub'>{ this.state.vehicle }</span>
				<span className='flight-item-sub'>{ this.state.days }</span>
			</span>
		);
	};
};
