class YandexPlanesShedule {

	_apiBase = 'https://cors-anywhere.herokuapp.com/https://api.rasp.yandex.net/v3.0/search/?apikey=4947ef3f-adeb-4221-93ec-743762d9d209&format=json&';

	async getResourse(url) {
		const res = await fetch(`${this._apiBase}${url}`);

		if (!res.ok) {
			throw new Error(`Could not fetch ${url}` +
			`, Received status: ${res.status}`);
		}
		return await res.json();
	};

	async getAllFlights() {
		const res = await this.getResourse(`from=c146&to=c213&lang=ru_RU&transport_types=plane`);
		return res.segments;
	};
};

const yandexAPI = new YandexPlanesShedule();

yandexAPI.getAllFlights().then((flights) => {
	flights.forEach(flight => {
		console.log(flight.arrival);
	})
});
