let xhr = new XMLHttpRequest();

xhr.open('GET', 'https://api.rasp.yandex.net/v3.0/search/?apikey=4947ef3f-adeb-4221-93ec-743762d9d209&format=json&from=c146&to=c213&lang=ru_RU&page=1&date=2018-11-29', false);

xhr.send();

if (xhr.status !== 200) {
  console.log( xhr.status + ': ' + xhr.statusText ); // пример вывода: 404: Not Found
} else {
  // вывести результат
  console.log( xhr.responseText ); // responseText -- текст ответа.
}
