import React, { Component } from 'react';

import Header from '../header';
import SearchPanel from '../search-panel';
import FlightsTable from '../flights-table';
import YandexFlightsShedule from '../../services/yandex-flights-shedule';

import './app.css';

export default class App extends Component {

  apiService = new YandexFlightsShedule();

  state = {
  	flightsData: null
  }

  constructor() {
		super();
		// this.getFlights();
	}

  getFlights() {
  	this.apiService.getDepartures()
  		.then((data) => {
  			// console.log(data);
  			this.setState(({ flightsData: data }));
  		});
  }

  render() {
  	return (
	    <div className='app'>
	      <Header />
	      <SearchPanel />
	      <FlightsTable 
	      	flightsData={ this.state.flightsData }
	      />
	    </div>
	  );
  }
};
